import { useState } from 'react';
import 'milligram';
import './App.css';

function App() {
	const [guess, setGuess] = useState("");
	const [guesses, setGuesses] = useState([]);
	const [evals, setEvals] = useState([]);
	const [message, setMessage] = useState("");
	const [currGuess, setCurrGuess] = useState(0);

	function genNum() {
		let nums = [];
		while (nums.length < 4) {
			let temp = Math.floor(Math.random() * 10);
			if (!nums.includes(temp)) {
				if (nums.length > 0 || temp != 0) {
					nums.push(temp);
				}
			}
		}
		return nums;
	}

	const [secretCode, setSecretCode] = useState(genNum);

	function updateGuess(ev) {
		setGuess(ev.target.value);
	}

	function isValidGuess() {
		let guessArr = guess.split("");
		//console.log(guessArr);
		if (guessArr.length != 4) {
			return false;
		}
		for (var i = 0; i < guessArr.length; i++) {
			if (typeof guessArr[i].valueOf() == "number") {
				return false;
			}
			for (var j = i + 1; j < guessArr.length; j++) {
				if (guessArr[i] == guessArr[j]) {
					return false;
				}
			}
		}
		return true;
	}

	function evaluateGuess() {
		let guessSplit = guess.split("");
		let exactCount = 0;
		let presentCount = 0;
		for (var k = 0; k < guessSplit.length; k++) {
			if (guessSplit[k] == secretCode[k]) {
				exactCount++;
			}
			for (var m = k + 1; m < guessSplit.length; m++) {
				if (guessSplit[k] == secretCode[m]) {
					presentCount++;
				}
			}
		}
		return exactCount + "A" + presentCount + "B";
	}

	function makeGuess() {
		if (isValidGuess()) {
			let newGuesses = guesses;
			newGuesses.push(guess);
			setGuesses(newGuesses);
			setEvals(evals.concat(evaluateGuess()));
			setMessage("");
			setCurrGuess(currGuess + 1);
		} else {
			setMessage(guess + " is an invalid guess. Try again");
		}
		setGuess("");
	}

	//source for keypress - class notes on making hangman game
	//github.com/NatTuck/scratch-2021-01/blob/master/notes-4550/04-react-intro/notes.md
	function keypress(ev) {
		if (ev.key == "Enter"){
			makeGuess();
		}
	}

	function gameWon() {
		if (currGuess === 0) {
			return false;
		}
		return evals[currGuess - 1].indexOf("4A") === 0;
	}

	function gameLost() {
		return guesses.length >= 8;
	}

	function newGame() {
		setGuesses([]);
		setEvals([]);
		setMessage("");
		setGuess("");
		setSecretCode(genNum);
		setCurrGuess(0);
	}

	function getGuess(index) {
		if (index >= guesses.length) {
			return " ";
		}
		return guesses[index];
	}

	function getEval(index) {
		if (index >= evals.length) {
			return " ";
		}
		return evals[index];
	}

	if (gameWon()) {
		return (
			<div className="App">
				<h1>You won! The secret code was {secretCode}</h1>
				<p>
					<button onClick={newGame}>New Game</button>
				</p>
			</div>
		);
	}

	if (gameLost()) {
		return (
			<div className="App">
				<h1>You ran out of guesses! The secret code was {secretCode}</h1>
				<p>
					<button onClick={newGame}>New Game</button>
				</p>
			</div>
		);
	}

	return (
		<div className="App">
			<h2>Bulls and Cows Game</h2>
			<div className="row">
				<div className="column">
					<h3>Guess</h3>
				</div>
				<div className="column">
					<h3>Result</h3>
				</div>
			</div>
			<div className="row">
				<div className="column">
					<p>{getGuess(0)}</p>
				</div>
				<div className="column">
					<p>{getEval(0)}</p>
				</div>
			</div>
			<div className="row">
				<div className="column">
					<p>{getGuess(1)}</p>
				</div>
				<div className="column">
					<p>{getEval(1)}</p>
				</div>
			</div>
			<div className="row">
				<div className="column">
					<p>{getGuess(2)}</p>
				</div>
				<div className="column">
					<p>{getEval(2)}</p>
				</div>
			</div>
			<div className="row">
				<div className="column">
					<p>{getGuess(3)}</p>
				</div>
				<div className="column">
					<p>{getEval(3)}</p>
				</div>
			</div>
			<div className="row">
				<div className="column">
					<p>{getGuess(4)}</p>
				</div>
				<div className="column">
					<p>{getEval(4)}</p>
				</div>
			</div>
			<div className="row">
				<div className="column">
					<p>{getGuess(5)}</p>
				</div>
				<div className="column">
					<p>{getEval(5)}</p>
				</div>
			</div>
			<div className="row">
				<div className="column">
					<p>{getGuess(6)}</p>
				</div>
				<div className="column">
					<p>{getEval(6)}</p>
				</div>
			</div>
			<div className="row">
				<div className="column">
					<p>{getGuess(7)}</p>
				</div>
				<div className="column">
					<p>{getEval(7)}</p>
				</div>
			</div>
			<p>You have {8-guesses.length} guesses left</p>
			<div className="row">
				<div className="column">
					<p><input type="text" value={guess} onChange={updateGuess} onKeyPress={keypress} /></p>
				</div>
				<div className="column">
					<p><button onClick={makeGuess}>Guess</button></p>
				</div>
			</div>
			<p>{message}</p>
			<p>
				<button onClick={newGame}>New Game</button>
			</p>
		</div>
	);
}

export default App;
